import { readFileSync, readdirSync, statSync } from 'fs';
import { join, relative } from 'path';

const skip = new Set\(['07_Spells.md', '12_MonstersA-Z.md', '13_Animals.md']\);
const srdDir = join\(process.cwd\(\), 'srd'\);
let content = '';
let totalLines = 0;

function walk\(dir\) {
  for \(const entry of readdirSync\(dir\)\) {
    const full = join\(dir, entry\);
    const stat = statSync\(full\);
    if \(stat.isDirectory\(\)\) walk\(full\);
    else if \(entry.endsWith\('.md'\) && !skip.has\(entry\)\) {
      const text = readFileSync\(full, 'utf-8'\);
      const lines = text.split\('\\n'\).length;
      totalLines += lines;
      content += text;
      console.log\(relative\(srdDir, full\) + ': ' + lines + ' lines, ' + text.length + ' chars'\);
    }
  }
}
walk\(srdDir\);
console.log\('\\nTotal: ' + totalLines + ' lines, ' + content.length + ' chars'\);
console.log\('Estimated tokens: ~' + Math.round\(content.length / 4\)\);
" 2>&1)
